INSERT INTO `wp_posts` VALUES (43,1,'2020-06-29 17:49:54','2020-06-29 17:49:54','\n					<!-- wp:cover {\"url\":\"http://cfpp.loc/wp-content/uploads/2020/06/hero-scaled.jpg\",\"id\":23,\"dimRatio\":0,\"customOverlayColor\":\"#ffffff\",\"align\":\"full\"} -->\n					<div class=\"wp-block-cover alignfull\" style=\"background-image:url(http://cfpp.loc/wp-content/uploads/2020/06/hero-scaled.jpg);background-color:#ffffff\"><div class=\"wp-block-cover__inner-container\"><!-- wp:heading {\"level\":1,\"align\":\"center\"} -->\n					<h1 style=\"text-align:center\">Welcome</h1>\n					<!-- /wp:heading -->\n\n					<!-- wp:paragraph {\"align\":\"center\",\"customTextColor\":\"#000000\"} -->\n					<p style=\"color:#000000;text-align:center\" class=\"has-text-color\">This is your homepage which is what most visitors will see when they first visit your shop.</p>\n					<!-- /wp:paragraph -->\n\n					<!-- wp:paragraph {\"align\":\"center\",\"customTextColor\":\"#000000\"} -->\n					<p style=\"color:#000000;text-align:center\" class=\"has-text-color\">You can change this text by editing the \"Welcome\" page via the \"Pages\" menu in your dashboard.</p>\n					<!-- /wp:paragraph --></div></div>\n					<!-- /wp:cover -->\n				\n\n				<!-- wp:heading {\"align\":\"center\"} -->\n				<h2 style=\"text-align:center\">Shop by Category</h2>\n				<!-- /wp:heading -->\n\n				<!-- wp:shortcode -->\n				[product_categories limit=\"3\" columns=\"3\" orderby=\"menu_order\"]\n				<!-- /wp:shortcode -->\n\n				<!-- wp:heading {\"align\":\"center\"} -->\n				<h2 style=\"text-align:center\">New In</h2>\n				<!-- /wp:heading -->\n\n				<!-- wp:woocommerce/product-new {\"columns\":4} -->\n				<div class=\"wp-block-woocommerce-product-new\">[products limit=\"4\" columns=\"4\" orderby=\"date\" order=\"DESC\"]</div>\n				<!-- /wp:woocommerce/product-new -->\n\n				\n					<!-- wp:heading {\"align\":\"center\"} -->\n					<h2 style=\"text-align:center\">We Recommend</h2>\n					<!-- /wp:heading -->\n\n					<!-- wp:woocommerce/handpicked-products {\"columns\":4,\"editMode\":false,\"products\":[32,34,35,36]} -->\n					<div class = \"wp-block-woocommerce-handpicked-products\">[products limit=\"4\" columns=\"4\" orderby=\"date\" order=\"DESC\" ids=\"32,34,35,36\"]</div>\n					<!-- /wp:woocommerce/handpicked-products -->\n				\n\n				<!-- wp:heading {\"align\":\"center\"} -->\n				<h2 style=\"text-align:center\">Fan Favorites</h2>\n				<!-- /wp:heading -->\n\n				<!-- wp:woocommerce/product-top-rated {\"columns\":4} -->\n				<div class=\"wp-block-woocommerce-product-top-rated\">[products limit=\"4\" columns=\"4\" orderby=\"rating\"]</div>\n				<!-- /wp:woocommerce/product-top-rated -->\n\n				<!-- wp:heading {\"align\":\"center\"} -->\n				<h2 style=\"text-align:center\">On Sale</h2>\n				<!-- /wp:heading -->\n\n				<!-- wp:woocommerce/product-on-sale {\"columns\":4} -->\n				<div class=\"wp-block-woocommerce-product-on-sale\">[products limit=\"4\" columns=\"4\" orderby=\"date\" order=\"DESC\" on_sale=\"1\"]</div>\n				<!-- /wp:woocommerce/product-on-sale -->\n\n				<!-- wp:heading {\"align\":\"center\"} -->\n				<h2 style=\"text-align:center\">Best Sellers</h2>\n				<!-- /wp:heading -->\n\n				<!-- wp:woocommerce/product-best-sellers {\"columns\":4} -->\n				<div class=\"wp-block-woocommerce-product-best-sellers\">[products limit=\"4\" columns=\"4\" best_selling=\"1\"]</div>\n				<!-- /wp:woocommerce/product-best-sellers -->','Homepage','','inherit','closed','closed','','28-revision-v1','','','2020-06-29 17:49:54','2020-06-29 17:49:54','',28,'http://cfpp.loc/2020/06/29/28-revision-v1/',0,'revision','',0);
/*!40000 ALTER TABLE `wp_posts` ENABLE KEYS */;
UNLOCK TABLES;
COMMIT;

